import React from 'react'
import "../styles/service1_1.css"
import "../styles/multipleChkbox.css"

let names=['propaganda0','propaganda1','propaganda2'];

class Multiplechkbox extends React.Component{

    render(){
        return(
        
        <div  style={{
            position: 'relative',
            left: '50%',
                    }}> 
            
            <table>
                <tbody>
<tr><td><input type="checkbox" name="propaganda1"  onClick={() => this.changerCouleur(names[1])}/> <mark id="propaganda1">Propaganda 1</mark></td></tr>
<tr><td><input type="checkbox" name="Propaganda 2"/> <mark id="propaganda2">Propaganda 2</mark></td></tr>
<tr><td><input type="checkbox" name="Propaganda 3"/> <mark id="propaganda3">Propaganda 3</mark></td></tr>
</tbody>
</table>
                
               
           

           

        </div>);
    }
   
    changerCouleur(name){
        console.log("test")
        console.log(name)
       let span = document.querySelectorAll("#res #"+name);
    
       span.forEach(function(sp) {
           sp.style.backgroundColor="white"
    });
       
    }

}

export default Multiplechkbox;
