//import { Link} from "react-router-dom";
//import Button from '../components/button';
//import ProductHero from '../components/productHero.js';

import Card from '../components/card';
import img1 from '../images/CABRIO_Elena.jpeg'
import img2 from '../images/Serena.jpg'
import img3 from '../images/vorakit.jpg'

import img4 from '../images/Yessine.jpg'
import "../styles/contact-us.css";








const ContactUs = () => {
    return (<>
     <div id="h1"  style={{marginBottom:'40px',fontFamily:'Georgia'}}>
        <div style={{backgroundColor:"rgba(0, 0, 0, 0.8)",width:"70%", margin: "0 auto"
,/*marginLeft:"220px"*/}}>
        <h1 className="snippets" style={{fontFamily:"Georgia"}}
          /*style={{
            display: "flex",
            justifyContent: "center",
            marginTop:"3%",
            fontSize:"3em"
          }}*/
        >
         Who we are
        </h1>
       
        <h2 className="test" style={{textAlign:"center",fontSize:"20px",marginRight:'10%',marginLeft:'10%',textShadow:'none'}}/*style={{
            display: "flex",
            justifyContent: "center",
            marginBottom: "6%",
            fontSize:'25px',
          }}*/>We are a group of researchers and students at Université Côte d'Azur, INRIA Sophia Antipolis, CNRS, I3S laboratory. We simple have a passion to make AI and language accessible to everyday life.</h2>
           </div>
          </div>
       
        <div style={{marginTop:'-60px'}} className="container-fluid d-flex justify-content-center">
        <div className="row" id="cardResponsive">
            <div className="col-md-3">
            <Card imgsrc={img1} title="Elena Cabrio" link="https://www-sop.inria.fr/members/Elena.Cabrio/" nameButton="More" text="Université Côte d’Azur, CNRS, Inria, I3S, France"/>
            
            </div>
            <div className="col-md-3">
    <Card imgsrc={img2} title="Serena Villata" link="https://www.i3s.unice.fr/~villata/index.html" nameButton="More" text="Université Côte d’Azur, CNRS, Inria, I3S, France"/>
            </div>
            <div className="col-md-3">
    <Card imgsrc={img3} title="Vorakit Vorakitphan"   nameButton="More" link="https://sites.google.com/view/vorakitvorakitphan" text="Université Côte d’Azur, CNRS, Inria, I3S, France"/>
            </div>
            <div className="col-md-3">
    <Card imgsrc={img4} title="Yessine Ben El Bey"   link="https://www.linkedin.com/in/yessinebenelbey/" nameButton="More" text="Université Côte d'Azur" />
            </div>
        </div>
    </div></>
    /*<>
    <div className="container2">
    <div  id="bg" style={{
     height:'700px'
              }}>
<div className="text">
        <h1>Propaganda</h1>
          </div>
    </div>
    </div>
    </>*/
    );
  };
  
  export default ContactUs;
