import React from 'react'
import "../styles/service1_1.css"
import "../styles/multipleChkbox.css"


class Multiplechkbox extends React.Component{
    constructor(props) {
        super(props);
        this.state = { style: "propaganda1" };
        this.bloodyMary = this.bloodyMary.bind(this);
      }
    render(){
        return(
        
        <div  style={{
            position: 'relative',
            left: '50%',
                    }}> 
            
            <table>
<tr><td><input type="checkbox" name="Propaganda 1" onClick={changerCouleur(this)}/> <mark id={this.state.style}>Propaganda 1</mark></td></tr>
<tr><td><input type="checkbox" name="Propaganda 2"/> <mark id="propaganda2">Propaganda 2</mark></td></tr>
<tr><td><input type="checkbox" name="Propaganda 3"/> <mark id="propaganda3">Propaganda 3</mark></td></tr>

</table>
                
               
           

           

        </div>);
    }
   


}
function changerCouleur(id){
    this.setState({
        style: { backgroundColor: 'white' },
        class: 'someclass'
      });
}
export default Multiplechkbox;
