import React from 'react'
import "../styles/service1_1.css"
import "../styles/multipleChkbox.css"


class Chkbox extends React.Component{
    constructor(props) {
        super(props);
        this.identifiant=this.props.identifiant;
        console.log(this.props.identifiant)
      }
    render(){
        return(
     
<tr><td><input type="checkbox"  onClick={() => this.changerCouleur(this.identifiant)}/> <mark id={this.identifiant}>Propaganda 1</mark></td></tr>
                
               
           

           

       );
    }
   
    changerCouleur(name){
        console.log("test")
        console.log(name)
       let span = document.querySelectorAll("#res #"+name);
    
       span.forEach(function(sp) {
           sp.style.backgroundColor="white"
    });
       
    }

}

export default Chkbox;
