      if(tab[i]===texte){
        cpt++
      }
    }
    if(cpt>1){
      return true;
    }else{
      return false;
    }
  }
  function duplicateSpan(tab,span){
    let cpt=0;
    for(let i=0;i<tab.length;i++){
      if(tab[i].span===span){
        cpt++
      }
    }
    if(cpt>1){
      return true;
    }else{
      return false;
    }
  }
