import React ,{useState}from 'react'
import Button from './button';
import "../styles/textInput.css";
import axios from 'axios'
import { Link,useHistory} from "react-router-dom";
import Loading from "../images/loading.gif"
import { element } from 'prop-types';

export var Reponse=""

function App() {
let history=useHistory();
const [data,setData]=useState(null)
const [print,setPrint]=useState(false)


  function getData(val)
  {
    console.log(val.target.value)
    setData(val.target.value)
    setPrint(false)
  }
  function sendData(){
    let div=document.getElementsByTagName("div")
    console.log("taille"+div.length)
    let img=document.getElementsByTagName("img")
    for(let i=0;i<img.length;i++){
      img.style.display='block'
    }


    /*axios.post('https://jsonplaceholder.typicode.com/posts',{'data':data})*/
    axios.post('http://127.0.0.1:5000/post',{'data':data,
    headers: {'Access-Control-Allow-Origin' : '*'}})
    .then(response=>{
      setTimeout(function(){  //Beginning of code that should run AFTER the timeout
        Reponse=response.data
      
        history.push('/service1_1')
    },5000);
      
     
      /*console.log(response.data)
      console.log(response);*/
    })
    /*.then(() => this.setState({ redirect: true }))*/
    .catch(error=>{
      console.log(error);
    })
  }

  /*async function getDonnees() {
    const reponse = await axios.get('http://127.0.0.1:5000/post')
    console.log(reponse.data)
}*/
  return (
    <><div style={{
        display: 'flex',  justifyContent:'center', alignItems:'center', height: '100vh',flexDirection:'column'
      }}>
     {
       print?
       <h1> {data}</h1>
       :null
     }
    <textarea className="textArea" onChange={getData} />
    
      <Button onClick={()=>sendData()} >Analyze</Button>
      <Link to="/service1_1" >
      
    </Link>
    {/*console.log(data)*/}
    </div>
    <img  style={{
      display: 'none'
    }} src={Loading} alt="loading..." />
    </>
  );
  
}

export default App;
