       let spanCourant=new Object();
       spanCourant.label=data.article701225819[i][j].label;
       spanCourant.span=data.article701225819[i][j].span;
       spanCourant.probability=data.article701225819[i][j].probability;
        spans.push(spanCourant)
      }
      }else{
        for(let v=0;v<data.article701225819[i][j].label.length;v++){

          let spanCourant=new Object();
          spanCourant.label=data.article701225819[i][j].label[v];
          spanCourant.probability=data.article701225819[i][j].probability[v];
          spanCourant.span=data.article701225819[i].text.substring(data.article701225819[i][j].start_char[v],data.article701225819[i][j].end_index[v]);
          spans.push(spanCourant)
        
          
        }
      }
    }
  }

  }    
   
  for(let i=0;i<spans.length;i++){
    console.log("SPANS "+spans[i].span)

  }

  
  return res;
 // console.log(data.article701225819[propagandas[0]]["span_1"].probability);

        
  //console.log(data[0].span_1)
}
function SimpleWordcloud() {
  const words = [
    {
      text: 'told',
      value: 64,
    },
    {
      text: 'mistake',
      value: 11,
    },
    {
      text: 'thought',
      value: 16,
    },
    {
      text: 'bad',
      value: 17,
    },
  ]
  const callbacks = {
    getWordColor: word => word.value > 50 ? "blue" : "red",
    onWordClick: console.log,
    onWordMouseOver: console.log,
    getWordTooltip: word => `${word.text} (${word.value}) [${word.value > 50 ? "good" : "bad"}]`,
  }
  const options = {
    rotations: 2,
    rotationAngles: [-90, 0],
  };
  const size = [600, 400];
   
  
  //let words=Datacloud()
  return <ReactWordcloud  callbacks={callbacks}
  options={options}
  size={size}
  words={words} />
}

 

 
  export default Service2_1;
