import React from 'react';
//import {Reponse} from '../components/textInput'
import Propagande from "../components/propagande"
import data from "../json/jsonmock.json"
import "../styles/service1_1.css"
import Button from '../components/button';
import Multiplechkbox from '../components/MultipleChkbox';









const Service1_1 = () => {
    return (
      <div
>
        <div>
        {  /* {Reponse}*/}
        
           <div id="res" style={{
                     position: 'absolute',

       right:'20%'
      }}>{test()} 
      <div id="btn"><Button style={{
        
        position: 'relative',
        left: '50%',
        marginTop:'2em'
        
        
      }}>Retour</Button>
      
      </div>
         

      </div> 
      <div style={{
        
        position: 'absolute',
        left: '80%',
        top:'20%',        
        
      }}>
           <Multiplechkbox/>
         </div>
         { /* <mark>test</mark>*/}
      </div>
      
    </div>
    );
  };
  
  function test(){
   

   
   let tableau=[]
   let tabSpan=[]
   let tabTexte=[]
   for(let index=0;index<data.length;index++){
    //tabSpan.push(data[index].span);
    tabTexte.push(data[index].text);
   }
   for(let index=0;index<data.length;index++){
    

    if( duplicateTexte(tabTexte,data[index].text,index)){
      //TOUT CE QUI NE FONCTIONNE PAS
      let cpt=1;
      tabSpan.push(data[index].span)
      while(index+cpt<data.length && data[index+cpt].text===data[index].text){
        tabSpan.push(data[index+cpt].span)
        console.log(data[index+cpt].span)
        cpt++;
        console.log(cpt)

      }
      //Trier le tableau de span A revoir on sait jamais
      tabSpan.sort(function compare(a, b) {
        if (data[index].text.indexOf(a) < data[index].text.indexOf(b) )
           return -1;
        if (data[index].text.indexOf(a) > data[index].text.indexOf(b) )
           return 1;
        return 0;
      }); 
      //Traiter chaque span
      let sans_doublons = Array.from(new Set(tabSpan));

      console.log("Sans doublon ")
      console.log(sans_doublons)
      console.log("tabSpan ")
      console.log(tabSpan)
     sans_doublons.sort(function compare(a, b) {
        if (data[index].text.indexOf(a) < data[index].text.indexOf(b) )
           return -1;
        if (data[index].text.indexOf(a) > data[index].text.indexOf(b) )
           return 1;
        return 0;
      }); 
      let boucle=0;
      let curseur=0;
      //A REVOIR
      while(boucle<sans_doublons.length && curseur<data[index].text.length){
        if(data[index].text.indexOf(sans_doublons[boucle])!==-1){
          tableau.push(data[index].text.substring(curseur,data[index].text.indexOf(sans_doublons[boucle])))
          let id="propaganda"+data[index].label
        tableau.push(<mark id={id}>{sans_doublons[boucle]}</mark>)
        curseur=data[index].text.indexOf(sans_doublons[boucle])+sans_doublons[boucle].length
        }
        //IF ICI
        if(index===3){
          //console.log(sans_doublons)
        }
        
        console.log(curseur)
        boucle++;
      }
      
      tableau.push(data[index].text.substring(curseur,data[index].text.length))


      console.log(data[index].text)
      console.log(sans_doublons)
      tabSpan.slice(0, tabSpan.length);
      sans_doublons.slice(0, sans_doublons.length);
      index=index+cpt+1;

       
    }
    else{
      if(data[index].label===0){
        //Pas de propagande
        tableau.push(data[index].text)
        tableau.push(" ");



      }
      else{
        //Un mot de propagande en une phrase
        let curseur=data[index].text.indexOf(data[index].span);
        let id="propaganda"+data[index].label
        tableau.push(data[index].text.substring(0,curseur));
        tableau.push(<mark id={id}>{data[index].span}</mark>)
        tableau.push(data[index].text.substring(curseur+data[index].span.length));
        tableau.push(" ");

        //console.log(data[index].text.substring(0,curseur))



      }
    }

  }
 /* let tab=[]
  tab.push(data[0].text);

  for(let i=1;i<data.length;i++){
    if(data[i].text!==data[i-1].text)
    tab.push(data[i].text);
  }
  return tab*/
  return tableau;
}

  function duplicateTexte(tab,texte,index){
    for(let i=0;i<tab.length;i++){
      let approche=i-index;
      if((approche===1 || approche===-1 )&&tab[i]===texte){
        return true;
      }
    }
  
  }
  
  function duplicateSpan(tab,span){
    for(let i=0;i<tab.length;i++){
      if(tab[i]===span){
        return true;
      }
    }
   
  }

  export default Service1_1;
