import React from 'react'
import "../styles/service1_1.css"
import "../styles/multipleChkbox.css"


class Chkbox extends React.Component{
    constructor(props) {
        super(props);
        this.state = { style: "propaganda1" };
      }
    render(){
        return(
        
        <div  style={{
            position: 'relative',
            left: '50%',
                    }}> 
          <input type="checkbox" name="Propaganda 1" onClick={this.changerCouleur(this)}/> <mark id={this.state.style}>Propaganda 1</mark>
                
               
           

           

        </div>);
    }
   
    changerCouleur(id){
        this.setState({
            style: "propaganda0",
          });
          console.log(id)
    }

}

export default Chkbox;
