        {  /* {Reponse}*/}
        
           <div id="res" style={{
       justifyContent:'center', alignItems:'center',flexDirection:'column'
      }}>{test()}    

      </div> 
      <div id="btn"><Button style={{
        position: 'absolute',
        left: '50%',
      }}>Retour</Button>
      </div>
         { /* <mark>test</mark>*/}
      </div>
      
    </div>
    );
  };
  
  function test(){
   

   
   let tableau=[]
   let after="";
   for(let index=0;index<data.length;index++){
    let id="propaganda"+data[index].label
    if(index<data.length-1){
      after=data[index+1].text
    }
     if(after===data[index].text){
        let tabTemp=[];
        let cpt=0;
        while(index+cpt<data.length && after===data[index+cpt].text){
          console.log(data[index+cpt].text)
          //Enlever les doublons
          if(data[index+cpt].span!==data[index].span){
            tabTemp.push(new Propagande(data[index+cpt].span,data[index+cpt].label))

          }
          cpt++;
        }
        
        if(tabTemp.length>1){

      
     }else{
      
      let numero=data[index].text.indexOf(data[index].span);
      let partie1="";
      let marque="";
      let fin="";
      for(let i=0;i<numero;i++){
        partie1=partie1+data[index].text.charAt(i).toString();
  
      }
      for(let i=0;i<data[index].span.length;i++){
        //console.log(Reponse.toString().charAt(i))
        marque=marque+data[index].text.charAt(i+numero).toString();
        }
      for(let i=numero+data[index].span.length;i<data[index].text.length;i++){
         //console.log(Reponse.toString().charAt(i))
         fin=fin+data[index].text.charAt(i).toString();
         }
        let marqueFinale=<mark id={id}>{data[index].span}</mark>
         tableau.push(<>{partie1}{marqueFinale}{fin}</>)
     }
   }
   return tableau
  }

