import React ,{useState}from 'react'
import Button from './button';
import "../styles/textInput.css";
import axios from 'axios'
import { Link,useHistory} from "react-router-dom";
//import Loading from "../images/loading.gif"

export var data;

function App() {
let history=useHistory();
const [input,setInput]=useState("")
const ColoredLine = ({ color }) => (
  <hr
      style={{
          color: color,
          backgroundColor: color,
          height: 2,
          width:900,
          marginLeft:'20%'
      }}
  />
);



 
  function sendData(){
    let img=document.getElementById("loading")
    img.style.display='block'

    let div=document.getElementById("divF")
    div.style.display='none'
    div=document.getElementById("h1")
    div.style.display='none'

    div=document.getElementById("example")
    div.style.display='none'

    let h1=document.getElementsByTagName("h1")
    for(let i=0;i<h1.length;i++){
      h1[i].style.display='none'
    }
    let h2= document.getElementsByTagName("h2");
    for (let i = 0; i < h1.length; i++) {
      h2[i].style.display = "none";
    }
    /*axios.post('https://jsonplaceholder.typicode.com/posts',{'data':data})*/
    if(!input.trim()){
      data=" "
      history.push("/PropagandaWordClouds2");
  
    }
    else{
    axios.post('http://127.0.0.1:5000/post',{id: 0,text:input,
    headers: {'Access-Control-Allow-Origin' : '*'}})
    .then(response=>{
      //setTimeout(function(){  //Beginning of code that should run AFTER the timeout
        data=response.data

        history.push('/PropagandaWordClouds2')
    //},5000);
      
     
      /*console.log(response.data)
      console.log(response);*/
    })
    /*.then(() => this.setState({ redirect: true }))*/
    .catch(error=>{
      console.log(error);
    })
  }
  }

  /*async function getDonnees() {
    const reponse = await axios.get('http://127.0.0.1:5000/post')
    console.log(reponse.data)
}*/
  return (
    <> <div id="h1" style={{marginBottom:'40px'}}><h1 className="snippets" /*style={{
      display: 'flex',  justifyContent:'center',marginTop:"3%"
    }}*/>Propaganda Word Clouds</h1>
     <h2 className="test" style={{textAlign:"center",fontSize:"20px",marginRight:'10%',marginLeft:'10%',textShadow:'none'}} /*style={{
            display: "flex",
            justifyContent: "center",
            marginBottom: "6%",
            fontSize:'25px',
          }}*/>Just type in some text or copy/paste it, push the button</h2>
          </div><div id="divF" style={{
      display: 'flex',  justifyContent:'center', alignItems:'center',flexDirection:'column'
    }}>
    
    <textarea  value={input} className="textArea" onChange={(e)=>{
          setInput(e.target.value)
          console.log(e.target.value);
      
        }} />
    <div>
      <Button  disabled={input === ""}
 onClick={()=>sendData()} >Analyze</Button>
      <Link to='/'>
      <Button style={{
        width:'98.48px'
      }}>back</Button>
      </Link>
      </div>
      <Link to="/service2_1" >
      
    </Link>
    {/*console.log(data)*/}
    </div>
    <div id="example">
    <ColoredLine color="blue"/> 
    <div /*style={{marginLeft:"30%"}}*/>
        <h3 style={{marginLeft:"42%"}}>Textual examples</h3>
        <h4 className="textExample" style={{fontSize:"1rem",marginBottom:"25px"}} style={{marginLeft:"42%"}}>Copy and paste an example below to give a try to identify propaganda snippets.</h4>
        <div className="col-container" /*style={{width:"50%", columnCount: '4',heigth:'90%',columnWidth:"300px"}}*/>
          <div className="col">
          <div id="box" content="Manchin says Democrats acted like babies at the SOTU (video) Personal Liberty Poll Exercise your right to vote." onClick={(element) => updateContent(element)} style={{backgroundColor:"#F8F8F8",marginBottom:"2px",border:"solid 1px",padding:"15px",fontFamily:"Arial Unicode MS, Arial, sans-serif",fontSize:"13px"}}>Manchin says Democrats acted like babies at the SOTU (video) Personal Liberty Poll Exercise your right to vote.
</div>
          <div id="box" onClick={(element) => updateContent(element)} content="Democrat West Virginia Sen. Joe Manchin says his colleagues’ refusal to stand or applaud during President Donald Trump’s State of the Union speech was disrespectful and a signal that the party is more concerned with obstruction than it is with progress." style={{backgroundColor:"#F8F8F8",marginBottom:"2px",border:"solid 1px",padding:"15px",fontFamily:"Arial Unicode MS, Arial, sans-serif",fontSize:"13px"}}>Democrat West Virginia Sen. Joe Manchin says his colleagues’ refusal to stand or applaud during President Donald Trump’s State of the Union speech was disrespectful and a signal that the party is more concerned with obstruction than it is with progress.</div>
          <div id="box" onClick={(element) => updateContent(element)} content="Another Republican who wants to stop investigating is former RNC Chair, Michael Steele: “There is no Spygate because there are no spies in the campaign.” The attitude of Steve Scalise is wishy-washy." style={{backgroundColor:"#F8F8F8",marginBottom:"2px",border:"solid 1px",padding:"15px",fontFamily:"Arial Unicode MS, Arial, sans-serif",fontSize:"13px"}}>Another Republican who wants to stop investigating is former RNC Chair, Michael Steele: “There is no Spygate because there are no spies in the campaign.” The attitude of Steve Scalise is wishy-washy.</div>
          </div>
          <div className="col">
          <div id="box" onClick={(element) => updateContent(element)} content="“It’s not necessary to agree with everything he says, it’s not necessary to approve of everything he does, but history will judge him as being on the right side of a struggle between good and evil,” Batten continued." style={{backgroundColor:"#F8F8F8",marginBottom:"2px",border:"solid 1px",padding:"15px",fontFamily:"Arial Unicode MS, Arial, sans-serif",fontSize:"13px"}}>“It’s not necessary to agree with everything he says, it’s not necessary to approve of everything he does, but history will judge him as being on the right side of a struggle between good and evil,” Batten continued.</div>
          <div id="box" onClick={(element) => updateContent(element)} content="She noticed that Latino voters did record-breaking numbers, especially in states like Florida, Nevada and Arizona – the last of which she describes as “a key state for us.” She brags that the company used its power to ensure that millions of people saw certain hashtags and social media impressions, with the goal of influencing their behavior during the election.”" style={{backgroundColor:"#F8F8F8",marginBottom:"2px",border:"solid 1px",padding:"15px",fontFamily:"Arial Unicode MS, Arial, sans-serif",fontSize:"13px"}}>She noticed that Latino voters did record-breaking numbers, especially in states like Florida, Nevada and Arizona – the last of which she describes as “a key state for us.” She brags that the company used its power to ensure that millions of people saw certain hashtags and social media impressions, with the goal of influencing their behavior during the election.”</div>
          <div id="box" onClick={(element) => updateContent(element)} content="Meanwhile, Google has yet to answer why their search results for the word “Idiot” are vastly different from DuckDuckGo." style={{backgroundColor:"#F8F8F8",marginBottom:"2px",border:"solid 1px",padding:"15px",fontFamily:"Arial Unicode MS, Arial, sans-serif",fontSize:"13px"}}>Meanwhile, Google has yet to answer why their search results for the word “Idiot” are vastly different from DuckDuckGo.</div>
          </div>
          <div className="col">
          <div id="box" onClick={(element) => updateContent(element)} content="But Democrats,including the committee’s ranking member, Rep. Adam Schiff, D-Calif., are blasting the declassification order as a “clear abuse of power.”" style={{backgroundColor:"#F8F8F8",marginBottom:"2px",border:"solid 1px",padding:"15px",fontFamily:"Arial Unicode MS, Arial, sans-serif",fontSize:"13px"}}>But Democrats,including the committee’s ranking member, Rep. Adam Schiff, D-Calif., are blasting the declassification order as a “clear abuse of power.”</div>
          <div id="box" onClick={(element) => updateContent(element)} content="I'm all for situations like Nikolas Cruz who assaulted teachers and students being arrested and tried and dealt with lawfully, but to go seizing people's property and denying them their liberty base on the hearsay of another person is anti-constitutional and anti-American." style={{backgroundColor:"#F8F8F8",marginBottom:"2px",border:"solid 1px",padding:"15px",fontFamily:"Arial Unicode MS, Arial, sans-serif",fontSize:"13px"}}>I'm all for situations like Nikolas Cruz who assaulted teachers and students being arrested and tried and dealt with lawfully, but to go seizing people's property and denying them their liberty base on the hearsay of another person is anti-constitutional and anti-American.</div>

          <div id="box" onClick={(element) => updateContent(element)} content="In a glaring sign of just how stupid and petty things have become in Washington these days, Manchin was invited on Fox News Tuesday morning to discuss how he was one of the only Democrats in the chamber for the State of the Union speech not looking as though Trump killed his grandma." style={{backgroundColor:"#F8F8F8",marginBottom:"20px",border:"solid 1px",padding:"15px",fontFamily:"Arial Unicode MS, Arial, sans-serif",fontSize:"13px"}}>In a glaring sign of just how stupid and petty things have become in Washington these days, Manchin was invited on Fox News Tuesday morning to discuss how he was one of the only Democrats in the chamber for the State of the Union speech not looking as though Trump killed his grandma.</div>
          </div>

          </div>  
          </div>  
        {/*console.log(data)*/}
      </div>
    </>
  );
