        let spanCourant={};
       spanCourant.text=data.article701225819[i][j].span;
       spanCourant.value=data.article701225819[i][j].probability;
       spanCourant.label=data.article701225819[i][j].label;

        spans.push(spanCourant)
      }
      }else{
        for(let v=0;v<data.article701225819[i][j].label.length;v++){

          let spanCourant={};
          spanCourant.text=data.article701225819[i].text.substring(data.article701225819[i][j].start_char[v],data.article701225819[i][j].end_index[v]);
          spanCourant.value=data.article701225819[i][j].probability[v];
          spanCourant.label=data.article701225819[i][j].label[v];

          spans.push(spanCourant)
        
          
        }
      }
    }
  }

  }    
  let colors=["yellow","red","green","magenta","maroon","sienna","orange","lime","#9B5D9B","aqua","pink","#8486D4","#5A6309","#D1C1F0"]

  for(let i=0;i<spans.length;i++){
    word.push({text: spans[i].text,value: spans[i].value*100,color:colors[spans[i].label-1]})
    

  }
  console.log(word)
  

