import React ,{useState}from 'react'
import Button from './button';
import "../styles/textInput.css";
import axios from 'axios'
import { Link,useHistory} from "react-router-dom";
//import Loading from "../images/loading.gif"

export var data;

function App() {
let history=useHistory();
const [data,setData]=useState(null)
const [print,setPrint]=useState(false)


  function getData(val)
  {
    console.log(val.target.value)
    setData(val.target.value)
    setPrint(false)
  }
  function sendData(){
    let img=document.getElementById("loading")
    img.style.display='block'

    let div=document.getElementById("divF")
    div.style.display='none'

    let h1=document.getElementsByTagName("h1")
    for(let i=0;i<h1.length;i++){
      h1[i].style.display='none'
    }
    /*axios.post('https://jsonplaceholder.typicode.com/posts',{'data':data})*/
    axios.post('http://127.0.0.1:5000/post',{'id': 0,'text':data,
    headers: {'Access-Control-Allow-Origin' : '*'}})
    .then(response=>{
      setTimeout(function(){  //Beginning of code that should run AFTER the timeout
        Reponse=response.data
        data=response.data

        history.push('/service2_1')
    },5000);
      
     
      /*console.log(response.data)
      console.log(response);*/
    })
    /*.then(() => this.setState({ redirect: true }))*/
    .catch(error=>{
      console.log(error);
    })
  }

  /*async function getDonnees() {
    const reponse = await axios.get('http://127.0.0.1:5000/post')
    console.log(reponse.data)
}*/
  return (
    <> <h1 style={{
      display: 'flex',  justifyContent:'center',marginBottom:'6%'
    }}>Input your text</h1><div id="divF" style={{
      display: 'flex',  justifyContent:'center', alignItems:'center',flexDirection:'column'
    }}>
     {
       print?
       <h1> {data}</h1>
       :null
     }
    <textarea className="textArea" onChange={getData} />
    <div>
      <Button onClick={()=>sendData()} >Analyze</Button>
      <Link to='/'>
      <Button style={{
        width:'98.48px'
      }}>back</Button>
      </Link>
      </div>
      <Link to="/service2_1" >
      
    </Link>
    {/*console.log(data)*/}
    </div>
   
    </>
  );
  
}

export default App;
