import React ,{useState}from 'react'
import Button from './button';
import "../styles/textInput.css";


function App() {
const [data,setData]=useState(null)
const [print,setPrint]=useState(false)

  function getData(val)
  {
    console.warn(val.target.value)
    setData(val.target.value)
    setPrint(false)
  }
  function sendData(){
    /*Première solution*/
   /* const requestOptions = {
      method: 'POST',
      headers: {'Accept' : 'application/json',
        'Content-Type': 'application/x-www-form-urlencoded' },
      body: JSON.stringify({data})
  };*/
  console.log(data);
  /*fetch('127.0.0.1:8888')
      .then(response => response.json())
      .then(data);*/


      /*Deuxième solution
      let result= fetch('127.0.0.2:8888',{
        method:'post',
        mode:'no-cors',
        headers:{
          'Accept':'application/json',
          'Content-type':'application/json',    
        },
        body: JSON.stringify({
          donnee:data
        })
      });
      console.log(result)
      */
      (async function fetchAsync () {
        let data = await (await fetch(`127.0.0.1:8888`,{
                                    method: `GET`,
                                    headers: {
                                        'authorization': 'BaseAuth W1lcmxsa='
                                    }
                                })).json();
                         
    })();

      
  }
  return (
    <div style={{
        display: 'flex',  justifyContent:'center', alignItems:'center', height: '100vh',flexDirection:'column'
      }}>
     {
       print?
       <h1> {data}</h1>
       :null
     }
    <textarea class="textArea" onChange={getData} />
    <Button onClick={()=>sendData()} >Analyze</Button>
    {/*console.log(data)*/}
    </div>
  );
}
export default App;
