import React from 'react';
//import {Reponse} from '../components/textInput'
import Propagande from "../components/propagande"
import data from "../json/jsonmock.json"
import "../styles/service1_1.css"
import Button from '../components/button';





let tabTest=[]
tabTest.push(new Propagande("bleu","debut","fin"));
tabTest.push(new Propagande("vert","debut2","fin2"));
tabTest.push(new Propagande("violet","debut3","fin3"));
tabTest.push(new Propagande("jaune","debut4","fin4"));


const Service1_1 = () => {
    return (
      <div
>
        <div>
        {  /* {Reponse}*/}
        
           <div id="res" style={{
       justifyContent:'center', alignItems:'center',flexDirection:'column'
      }}>{test()}    

      </div> 
      <div id="btn"><Button style={{
        position: 'absolute',
        left: '50%',
      }}>Retour</Button>
      </div>
         { /* <mark>test</mark>*/}
      </div>
      
    </div>
    );
  };
  
  function test(){
   

   
   let tableau=[]
   let after="";
   for(let index=0;index<data.length;index++){
    let id="propaganda"+data[index].label
    if(index<data.length-1){
      after=data[index+1].text
    }
     if(after===data[index].text){
        let tabTemp=[];
        let cpt=0;
        while(index+cpt<data.length && after===data[index+cpt].text){
          console.log(data[index+cpt].text)
          //Enlever les doublons
          if(data[index+cpt].span!==data[index].span){
            tabTemp.push(new Propagande(data[index+cpt].span,data[index+cpt].label))

          }
          cpt++;
        }
        
        if(tabTemp.length>1){

       
        tabTemp.sort(function compare(a, b) {
          if (data[index].text.indexOf(a.label) < data[index].text.indexOf(b.label) )
             return -1;
          if (data[index].text.indexOf(a.label) > data[index].text.indexOf(b.label) )
             return 1;
          return 0;
        }); 
        let curseur=0;
        //console.log("valeur dans l'ordre tab temp "+tabTemp[0].texte+" ")
        while(curseur<data[index].text.length){

          for(let j=0;j<tabTemp.length;j++){
          tableau.push(data[index].text.substring(curseur,data[index].text.indexOf(tabTemp[j].texte)))
          //Changer la couleur ici
          //<mark id={id}>{data[index].span}</mark>
         id="propaganda"+tabTemp[j].label
         //console.log(tabTemp[j].texte)
          tableau.push(<mark id={id}>{tabTemp[j].texte}</mark>) 
          curseur=data[index].text.indexOf(tabTemp[j].texte)+tabTemp[j].texte.length;
          if(j===tabTemp.length-1){
            tableau.push(data[index].text.substring(curseur))
            curseur=data[index].text.length;
          }
          }}
          index=index+tabTemp.length;
        }
      
     }else{
      
      let numero=data[index].text.indexOf(data[index].span);
      let partie1="";
      let marque="";
      let fin="";
      for(let i=0;i<numero;i++){
        partie1=partie1+data[index].text.charAt(i).toString();
  
      }
      for(let i=0;i<data[index].span.length;i++){
        //console.log(Reponse.toString().charAt(i))
        marque=marque+data[index].text.charAt(i+numero).toString();
        }
      for(let i=numero+data[index].span.length;i<data[index].text.length;i++){
         //console.log(Reponse.toString().charAt(i))
         fin=fin+data[index].text.charAt(i).toString();
         }
        let marqueFinale=<mark id={id}>{data[index].span}</mark>
         tableau.push(<>{partie1}{marqueFinale}{fin}</>)
     }
   }
   return tableau
  }

  function getText1propaganda(data){
    //data[index]=data
    let numero=data.text.indexOf(data.span);
      let partie1="";
      let marque="";
      let fin="";
      let id="propaganda"+data.label

      for(let i=0;i<numero;i++){
        partie1=partie1+data.text.charAt(i).toString();
  
      }
      for(let i=0;i<data.span.length;i++){
        //console.log(Reponse.toString().charAt(i))
        marque=marque+data.text.charAt(i+numero).toString();
        }
      for(let i=numero+data.span.length;i<data.text.length;i++){
         //console.log(Reponse.toString().charAt(i))
         fin=fin+data.text.charAt(i).toString();
         }
        let marqueFinale=<mark id={id}>{data.span}</mark>
         tableau.push(<>{partie1}{marqueFinale}{fin}</>)
     }
  
  /*function getText(Reponse,fin){
