import React from 'react';
//import {Reponse} from '../components/textInput'
import Propagande from "../components/propagande"
import data from "../json/jsonmock.json"
import "../styles/service1_1.css"
import Button from '../components/button';





let tabTest=[]
tabTest.push(new Propagande("bleu","debut","fin"));
tabTest.push(new Propagande("vert","debut2","fin2"));
tabTest.push(new Propagande("violet","debut3","fin3"));
tabTest.push(new Propagande("jaune","debut4","fin4"));


const Service1_1 = () => {
    return (
      <div
>
        <div>
        {  /* {Reponse}*/}
        
           <div id="res" style={{
       justifyContent:'center', alignItems:'center',flexDirection:'column'
      }}>{test()}    

      </div> 
      <div id="btn"><Button style={{
        position: 'absolute',
        left: '50%',
      }}>Retour</Button>
      </div>
         { /* <mark>test</mark>*/}
      </div>
      
    </div>
    );
  };
  
  function test(){
    /*tabTest.sort(function compare(a, b) {
      if (Reponse.toString().indexOf(a.debut) < Reponse.toString().indexOf(b.debut) )
         return -1;
      if (Reponse.toString().indexOf(a.debut) > Reponse.toString().indexOf(b.debut) )
         return 1;
      return 0;
    });
  let tableau = [];
  let curseur=0;
  while(curseur<Reponse.length){
      
    for(let j=0;j<tabTest.length;j++){
    tableau.push(Reponse.toString().substring(curseur,Reponse.toString().indexOf(tabTest[j].debut)))
    //Changer la couleur ici
    tableau.push(<mark key={tabTest[j].debut}>{tabTest[j].getText(Reponse)}</mark>)
    curseur=Reponse.toString().indexOf(tabTest[j].fin)+tabTest[j].fin.length;
    if(j===tabTest.length-1){
      tableau.push(Reponse.toString().substring(curseur))
      curseur=Reponse.length;
    }
    }
  }
  return <>{tableau}</>;*/

   //return test2;
   let tableau=[]
   let after="";
   data.forEach((post,index)=>{
    let id="propaganda"+post.label
    if(after===data[index].text){
     /* let curseur=0;
      while(curseur<post.text.length){
          
        for(let j=0;j<post.text;j++){
        tableau.push(post.text.substring(curseur,post.text.indexOf(post.span)))
        //Changer la couleur ici
        tableau.push(<mark key={post.span}>{getText(post,data[index].post.text)}</mark>)
        curseur=post.text.indexOf(data[index].post.span)+data[index].post.span.length;
        if(j===tabTest.length-1){
          tableau.push(post.text.substring(curseur))
          curseur=post.text.length;
        }
        }
      }*/
    }else{
      after=post.text
      let numero=post.text.indexOf(post.span);
      let partie1="";
      let marque="";
      let fin="";
      for(let i=0;i<numero;i++){
        partie1=partie1+post.text.charAt(i).toString();
  
      }
      for(let i=0;i<post.span.length;i++){
        //console.log(Reponse.toString().charAt(i))
        marque=marque+post.text.charAt(i+numero).toString();
        }
      for(let i=numero+post.span.length;i<post.text.length;i++){
         //console.log(Reponse.toString().charAt(i))
         fin=fin+post.text.charAt(i).toString();
         }
        let marqueFinale=<mark id={id}>{post.span}</mark>
         tableau.push(<>{partie1}{marqueFinale}{fin}</>)

    }
   

   })
   return tableau
  }

  function getText(Reponse,fin){
    let numero=Reponse.text.indexOf(Reponse.span);
    let numero2=Reponse.text.indexOf(this.fin)+this.fin.length;
    let marque="";

    for(let i=numero;i<numero2;i++){
       //console.log(Reponse.toString().charAt(i))
       marque=marque+Reponse.toString().charAt(i).toString();
       }
       return marque;
}


  export default Service1_1;
