import React from 'react'
import "../styles/service1_1.css"
var couleurs = {
    1: ["#ffffcc","#ffff99","#ffff66","#ffff32","#ffff00"],
    2: ["#ffe5e5","#ffb2b2","#ff7f7f","#ff6666","#ff0000"],
    3: ["#e5f2e5","#b2d8b2","#7fbf7f","#4ca64c","#008000"],
    4: ["#ffb2ff","#ff99ff","#ff7fff","#ff4cff","#ff00ff"],
    5:["#e5cccc","#cc9999","#b26666","#993232","#800000"],
    6:["#e4e4e4","#c9c9c9","#afafaf","#949494","#7a7a7a"],
    7:["#ffedcc","#ffdb99","#ffc966","#ffb732","#ffa500"],
    8:["#ccffcc","#99ff99","#66ff66","#32ff32","#00ff00"],
    9:["#ebdeeb","#d7bed7","#c39dc3","#af7daf","#9b5d9b"],
    10:["#ccffff","#99ffff","#66ffff","#32ffff","#00ffff"],
    11:["#fff2f4","#ffe5ea","#ffd9df","#ffccd5","#ffc0cb"],
    12:["#e6e6f6","#cdceed","#b5b6e5","#9c9edc","#8486d4"],
    13:["#dedfcd","#bdc09c","#9ca16b","#7a823a","#5a6309"],
    14:["#ece6f9","#ded3f4","#dacdf3","#d5c7f1","#d1c1f0"]};

class Chkbox extends React.Component{
    constructor(props) {
        super(props);
        this.identifiant=this.props.identifiant;
        this.color=this.props.color;
        this.checked=true;
        this.label=this.props.label;
      }
    render(){
        return(
<tr><td style={{
      display:"inline-flex",
      }} ><input name={this.identifiant} type="checkbox"  onClick={() => this.changerCouleur(this.identifiant,this.color,this.label)} defaultChecked={this.checked}/>     <div class="chart">
      <div style={{
      backgroundColor:couleurs[this.label][0],
      width:"25%"
    }}></div>
      <div  style={{
      backgroundColor:couleurs[this.label][1],
      width:"25%"
    }}></div>
      <div style={{
      backgroundColor:couleurs[this.label][2],
      width:"25%"
    }}></div>
      <div style={{
      backgroundColor:[this.label][3],
      width:"25%"
    }}></div>
      <div style={{
      backgroundColor:[this.label][4],
      width:"25%"
    }}></div>
    
    </div><div>{this.props.name}</div></td></tr>
                
               
           

           

       );
    }
  
    changerCouleur(name,color,labelc){
        let colors=["yellow","red","green","magenta","maroon","sienna","orange","lime","#9B5D9B","aqua","pink","#8486D4","#5A6309","#D1C1F0"]
        let propagandas=["Appeal_to_Authority","Appeal_to_fear-prejudice","Bandwagon,Reductio_ad_hitlerum","Black-and-White_Fallacy","Causal_Oversimplification","Doubt","Exaggeration,Minimisation","Flag-Waving","Loaded_Language","Name_Calling,Labeling","Repetition","Slogans","Thought-terminating_Cliches","Whataboutism,Straw_Men,Red_Herring"]

        //var textinputs = document.querySelectorAll('input[type=checkbox]'); 
        //console.log("TextInputs"+textinputs.length)
       /* var empty = [].filter.call( textinputs, function( el ) {
            return !el.checked
         });*/
        /* let identifiantNotCheckd=[]
         for(let i=0;i<empty.length;i++){
            identifiantNotCheckd = empty[i].name.substring("propaganda".length)
         }
      let identifiant=name.substring("propaganda".length)*/
    if(document.querySelector('input[name="'+name+'"]').checked===true){
        let span = document.querySelectorAll("#res #"+name);
        
        span.forEach(function(sp) {
            sp.style.backgroundColor=color;
            sp.title=sp.getAttribute("title2")
     });
     span=document.querySelectorAll("#res #propaganda99");
     span.forEach(function(sp) {
        console.log("labels "+sp.getAttribute("labels")) 
        let labels=sp.getAttribute("labels").split(',')
        let labels2=sp.getAttribute("labels2").split(',')
        //console.log(labels)
        //console.log('taille '+labels.length)
        if(labels2.includes(labelc)){
            labels.push(labelc)
        }
        if(labels.includes("")){
            let indice=labels.indexOf("")
            labels.splice(indice,1)
            
        }
        //console.log(labels)

 if(labels2.includes(labelc)){
        sp.setAttribute("labels",labels)
        if(labels.length===1){
            sp.style.textDecoration="none"
            sp.style.fontWeight="normal"


            sp.style.backgroundColor=colors[labelc-1];
            let titre=sp.getAttribute("title2").split("\n");
            for(let t=0;t<titre.length;t++){
                if(titre[t].split(" - ")[0]===propagandas[labelc-1]){
                    sp.setAttribute("title",propagandas[labelc-1]+" - "+titre[t].split(" - ")[1]+"\n")
                }
            }
       
            //sp.title=sp.getAttribute("title2")

        }else if(labels.length===0){
            sp.style.background="none";
            sp.style.textDecoration="none"
            sp.style.fontWeight="normal"


            sp.removeAttribute("title")


        }else{
            //sp.style.backgroundColor="#fcf8e3";
            sp.style.background="none"

            //sp.style.textDecoration="underline red"
            sp.style.fontWeight="bold"


            let titre2=sp.getAttribute("title");
            let titre=sp.getAttribute("title2").split("\n");
            if(titre2.indexOf(propagandas[labelc-1])===-1){
            for(let t=0;t<titre.length;t++){
                if(titre[t].split(" - ")[0]===propagandas[labelc-1]){
                    sp.setAttribute("title",titre2+propagandas[labelc-1]+" - "+titre[t].split(" - ")[1]+"\n")
                }
            }
        }
        }
    }
       /* sp.style.backgroundColor=color;
        sp.title=sp.getAttribute("title2")*/
 });
     
    }else{
        let span = document.querySelectorAll("#res #"+name);
    
        span.forEach(function(sp) {
            sp.style.background="none"
            sp.removeAttribute("title")
     });
     span=document.querySelectorAll("#res #propaganda99");
     span.forEach(function(sp) {
        let labels=sp.getAttribute("labels").split(',')

        let labels2=sp.getAttribute("labels2").split(',')
        
        if(labels2.includes(labelc)){
            let indice=labels.indexOf(labelc)
            labels.splice(indice,1)
        }
        console.log("labels new "+labels)

        sp.setAttribute("labels",labels)

        if(labels.length===1){
            //console.log("labels 0 "+labels[0])
            sp.style.textDecoration="none";
            sp.style.fontWeight="normal"

            sp.style.backgroundColor=colors[labels[0]-1];
            let titre="";
           if(sp.getAttribute("title")!==null){
             titre=sp.getAttribute("title").split("\n");

           }
            let res=""
            for(let t=0;t<titre.length-1;t++){
                if(titre[t].split(" - ")[0]!==propagandas[labelc-1]){
                   
                    res=res+titre[t]+"\n"
                    
                }

            }
            sp.setAttribute("title",res)
        
            //sp.title=sp.getAttribute("title2")

        }else if(labels.length===0){
            sp.style.textDecoration="none";
            sp.style.fontWeight="normal"

            sp.style.background="none"
            sp.removeAttribute("title")


        }
        else{
            let titre=""
            if(sp.getAttribute("title")!==null){
            titre=sp.getAttribute("title").split("\n");
            }
            let res=""
            for(let t=0;t<titre.length-1;t++){
                console.log("split espace",titre[t].split(" - ")[0]+titre[t].split(" - ")[0].length)
                console.log("propagande "+propagandas[labelc-1]+" "+(propagandas[labelc-1]).length)
                if(titre[t].split(" - ")[0]!==propagandas[labelc-1]){
                   
                    res=res+titre[t]+"\n"
                    
                }
            }
            sp.setAttribute("title",res)

            //sp.style.backgroundColor="#fcf8e3";
            sp.style.background="none"
            //sp.style.textDecoration="underline red"
            sp.style.fontWeight="bold"

        }
       /* sp.style.backgroundColor=color;
        sp.title=sp.getAttribute("title2")*/
 });
   
    }
    //console.log("CHECKED "+document.querySelector('input[name="'+name+'"]').checked)
    }

}

export default Chkbox;
