import React ,{useState}from 'react'
import Button from './button';
import "../styles/textInput.css";
import axios from 'axios'
import { Link,useHistory} from "react-router-dom";
import { send } from 'process';
const { exec } = require('child_process')
export var data;
//const cp = require('child_process');

function App() {
let history=useHistory();
let etatButton=true
const [data2,setData]=useState(null)
const [print,setPrint]=useState(false)


  function getData(val)
  {
    console.log(val.target.value)
    let btn;

    if(val.target.value===undefined || val.target.value===""){
     btn=document.getElementById("boutonData").disabled=true

    }else{
      document.getElementById("boutonData").removeAttribute("disabled");
      document.getElementById("boutonData").onClick = function() { sendData() };
    }
    setData(val.target.value)
    setPrint(false)
   // btn=document.getElementById("boutonData").disabled=true
    
    console.log(btn)
    
    
  }
  
  function sendData(){
    let img=document.getElementById("loading")
    img.style.display='flex'

    let div=document.getElementById("divF")
    div.style.display='none'

    let h1=document.getElementsByTagName("h1")
    for(let i=0;i<h1.length;i++){
      h1[i].style.display='none'
    }
    /*const cmd = cp.spawn('cmd');
    cmd.on('exit',(data)=>{
      console.log(data);
    });*/
    
    /*exec("dir", (error, stdout, stderr) => {
      if (error) {
          console.log(`error: ${error.message}`);
          return;
      }
      if (stderr) {
          console.log(`stderr: ${stderr}`);
          return;
      }
      console.log(`stdout: ${stdout}`);
  });*/
/*exec('dir', (err, stdout, stderr) => {
  if (err) {
    // node couldn't execute the command
    return;
  }

  // the *entire* stdout and stderr (buffered)
  console.log(`stdout: ${stdout}`);
  console.log(`stderr: ${stderr}`);
});*/
    //h1.style.display='none'
    /*let file = open('/path/to/your/file.py', 'r').read()
    console.log(exec(file))*/
    /*var exec = require('child_process').exec, child;

    child = exec('c:/Users/dofla/Desktop/pythonTest/python.py',
        function (error, stdout, stderr) {
            console.log('stdout: ' + stdout);
            console.log('stderr: ' + stderr);
            if (error !== null) {
                 console.log('exec error: ' + error);
            }
        });
     child();*/
    /*axios.post('https://jsonplaceholder.typicode.com/posts',{'data':data})*/
    console.log("data2"+data2)
    axios.post('http://127.0.0.1:5000/post',{'id': 0,'text':data2,
    headers: {'Access-Control-Allow-Origin' : '*'}})
    .then(response=>{
      setTimeout(function(){  //Beginning of code that should run AFTER the timeout
        console.log("response \n")
        console.log(response)
        //Reponse=response.data
       data=response.data
        history.push('/service1_1')
    },5000);
      
     
      /*console.log(response.data)
      console.log(response);*/
    })
    /*.then(() => this.setState({ redirect: true }))*/
    .catch(error=>{
      console.log(error);
    })
  }

  /*async function getDonnees() {
    const reponse = await axios.get('http://127.0.0.1:5000/post')
    console.log(reponse.data)
}*/
  return (

    <>
    <div>
    <h1 style={{
        display: 'flex',  justifyContent:'center',marginBottom:'6%'
      }}>Input your text</h1>
</div><div id="divF" style={{
        display: 'flex',  justifyContent:'center', alignItems:'center',flexDirection:'column'
      }}>
     {
       print?
       <h1> {data}</h1>
       :null
     }
    <textarea className="textArea" onChange={getData} />
    <div>
      <Button id="boutonData" onClick={()=>sendData()} disabled={true}>Analyze</Button>
      <Link to='/'>
      <Button style={{
        width:'98.48px'
      }}>back</Button>
      </Link>
      </div>
      <Link to="/service1_1" >
      
    </Link>
    {/*console.log(data)*/}
    </div>
  
    </>
  );
  
}

export default App;
