import React from 'react'
import "../styles/navbar.css"
import Image from "../images/i3s.png";
import Cnrs from "../images/cnrs.png";
import Univ from "../images/cotedazur.png";
import Wimmics from "../images/wimmics.png";
import Inria from "../images/inria.png";




import {

    BrowserRouter as Router,

    Switch,

    Route,

  } from "react-router-dom";

  import { Navbar,Nav,NavDropdown } from 'react-bootstrap'

  


class BootstrapNavbar extends React.Component{


    render(){

        return(

            <div  onLoad= {() => handleResize()}>

                <div className="row">

                    <div className="col-md-12">

                        <Router>

                            <Navbar bg="light" variant="light" expand="lg" sticky="top">

                               <div className="logo">
                                   <Navbar.Brand href="https://univ-cotedazur.eu/">      
                                   <img src={Image} alt="logo" height="60"  style={{ marginRight:'1em'}}></img>
                                   </Navbar.Brand>
                                   <Navbar.Brand href="https://univ-cotedazur.eu/">  
                                   <img src={Cnrs} alt="logo" height="60" style={{ marginRight:'1em'}}></img>
    
                                   </Navbar.Brand>
                                   <Navbar.Brand href="https://univ-cotedazur.eu/">  
                                   <img src={Univ} alt="logo" height="60" style={{ marginRight:'1em'}}></img>    
                                   </Navbar.Brand>

                                   <img src={Wimmics} alt="logo" height="60"style={{ marginRight:'1em'}}></img>
                                   <img src={Inria} alt="logo" height="60"style={{ marginRight:'1em'}}></img>




                                </div> 

                                <Navbar.Toggle aria-controls="basic-navbar-nav"  />

                                <Navbar.Collapse id="basic-navbar-nav">
                                <div className="navbarY">
                                    <Nav className="mr-auto" >

                                        <Nav.Link href="/">Home</Nav.Link>
                                        <NavDropdown title="Services" id="basic-nav-dropdown">

                                            <NavDropdown.Item href="/service1">Service 1</NavDropdown.Item>
                                            <NavDropdown.Item href="/service2">Service 2</NavDropdown.Item>
                                            <NavDropdown.Item href="#action/3.3">Service 3</NavDropdown.Item>

                                            {/*<NavDropdown.Divider />*/
                                            }
                                            {/*<NavDropdown.Item href="#action/3.4">Separated link</NavDropdown.Item>*/
                                            }
                                        </NavDropdown>

                                        <Nav.Link href="/about-us">About</Nav.Link>
                                        <Nav.Link href="/contact-us">Contact Us</Nav.Link>

                                   
                                    </Nav>

                                </div>

                                </Navbar.Collapse>

                            </Navbar>

                            <br />

                            <Switch>

                                <Route exact path="/">

                                    {/*<Home />*/}

                                </Route>

                                <Route path="/about-us">

                                   {/* <AboutUs />*/}

                                </Route>

                                <Route path="/contact-us">

                                    {/*<ContactUs />*/}

                                </Route>

                            </Switch>

                        </Router>

                    </div>

                </div>
                
                
                {window.addEventListener('resize', handleResize)}

            </div>
            

        )  
        
    }
   
}

function handleResize(){
    //console.log('resized to: ', window.innerWidth, 'x', window.innerHeight)
    if(window.innerWidth<992){
      document.querySelector('.navbarY').style.position="relative";
      document.querySelector('.navbarY').style.right=0;
    }
    else{
        document.querySelector('.navbarY').style.position="absolute";
        document.querySelector('.navbarY').style.right=10;

    }
   
}
export default BootstrapNavbar;
