import React from 'react'
import "../styles/service1_1.css"
import "../styles/multipleChkbox.css"


class Chkbox extends React.Component{
    constructor(props,id) {
        super(props);
        this.state = { style: {id}};
        this.id=id;
      }
    render(){
        return(
     
<tr><td><input type="checkbox"  onClick={this.changerCouleur}/> <mark id={this.id}>Propaganda 1</mark></td></tr>
                
               
           

           

       );
    }
   
    changerCouleur(id){
        this.setState({
            style: "propaganda0",
          });
          console.log(id)
    }

}

export default Chkbox;
