import React, { useState } from "react";
import Button from "./button";

import "../styles/textInput.css";
import axios from "axios";
import { Link, useHistory } from "react-router-dom";
export var data;
let id2 = 0;
function App() {
  let history = useHistory();
  const [input, setInput] = useState("");
  const ColoredLine = ({ color }) => (
    <hr
      style={{
        color: color,
        backgroundColor: color,
        height: 2,
        width: 900,
      }}
    />
  );

  function sendData() {
    //On fait disparaitre tous les Ã©lements et apparaitre le GIF de chargement
    let img = document.getElementById("loading");
    img.style.display = "flex";

    let div = document.getElementById("divF");
    div.style.display = "none";
    div = document.getElementById("h1");
    div.style.display = "none";
    div = document.getElementById("footer");
    div.style.display = "none";
    let h1 = document.getElementsByTagName("h1");
    for (let i = 0; i < h1.length; i++) {
      h1[i].style.display = "none";
    }
    let h2 = document.getElementsByTagName("h2");
    for (let i = 0; i < h1.length; i++) {
      h2[i].style.display = "none";
    }

    if (!input.trim()) {
      data = " ";
      let div = document.getElementById("footer");

      div.style = " display: visible";
      history.push("/PropagandaSnippetsDetection2");
    } else {
      id2++;
      axios
        .post("http://127.0.0.2:5000/post", {
          id: id2,
          text: input,
          headers: { "Access-Control-Allow-Origin": "*" },
        })
        .then((response) => {
          data = response.data;
          let div = document.getElementById("footer");

          div.style = " display: visible";
          history.push("/PropagandaSnippetsDetection2");
        })

        .catch((error) => {
          console.log(error);
        });
    }
  }

  return (
    <>
      <div>
        <div id="h1" style={{ marginBottom: "40px", fontFamily: "Georgia" }}>
          <div
            style={{
              backgroundColor: "rgba(0, 0, 0, 0.8)",
              width: "70%",
              margin: "0 auto",
            }}
          >
            <h1
              className="snippets"
              style={{ fontFamily: "Georgia" }}
            >
              Propaganda Snippets Detection
            </h1>

            <h2
              className="test"
              style={{
                textAlign: "center",
                fontSize: "20px",
                marginRight: "10%",
                marginLeft: "10%",
                textShadow: "none",
                paddingBottom: "20px",
              }}
            >
              Just type in some text or copy/paste an example below, push the
              button.
            </h2>
          </div>
        </div>
      </div>
      <div
        id="divF"
        style={{
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
          flexDirection: "column",
        }}
      >
        <textarea
          value={input}
          className="textArea"
          onChange={(e) => {
            setInput(e.target.value);
          }}
        />
        <div>
          <Button
            id="boutonData"
            disabled={input === ""}
            onClick={() => sendData()}
            style={{ marginRight: "10px" }}
          >
            Analyze
          </Button>

          <Link to="/">
            <Button
              style={{
                width: "98.48px",
              }}
            >
              back
            </Button>
          </Link>
        </div>
        <ColoredLine color="white" />
        <div
          style={{
            width: "58%",
            backgroundColor: "#D9D9D9",
            marginBottom: "2%",
          }}
        >
          <div
            style={{
              textAlign: "center",
              display: "flex",
              justifyContent: "center",
              alignItems: "center",
              flexDirection: "column",
              width: "150%",
              marginLeft: "-25%",
            }}
          >
            <h3 style={{ marginTop: "20px" }}>Textual examples</h3>
            <h4
              className="textExample"
              style={{ fontSize: "1rem", marginBottom: "25px" }}
            >
              Click one a sample below to copy and try to identify propaganda
              snippets.
            </h4>
            <div
              className="col-container"
            >
              <div className="col">
                <div
                  id="box"
                  content="Manchin says Democrats acted like babies at the SOTU (video) Personal Liberty Poll Exercise your right to vote."
                  onClick={(element) => updateContent(element)}
                  style={{
                    backgroundColor: "#F8F8F8",
                    marginBottom: "2px",
                    border: "solid 1px",
                    padding: "15px",
                    fontFamily: "Arial Unicode MS, Arial, sans-serif",
                    fontSize: "13px",
                  }}
                >
                  Manchin says Democrats acted like babies at the SOTU (video)
                  Personal Liberty Poll Exercise your right to vote.
                </div>
                <div
                  id="box"
                  onClick={(element) => updateContent(element)}
                  content="Democrat West Virginia Sen. Joe Manchin says his colleaguesâ refusal to stand or applaud during President Donald Trumpâs State of the Union speech was disrespectful and a signal that the party is more concerned with obstruction than it is with progress."
                  style={{
                    backgroundColor: "#F8F8F8",
                    marginBottom: "2px",
                    border: "solid 1px",
                    padding: "15px",
                    fontFamily: "Arial Unicode MS, Arial, sans-serif",
                    fontSize: "13px",
                  }}
                >
                  Democrat West Virginia Sen. Joe Manchin says his colleaguesâ
                  refusal to stand or applaud during President Donald Trumpâs
                  State of the Union speech was disrespectful and a signal that
                  the party is more concerned with obstruction than it is with
                  progress.
                </div>
                <div
                  id="box"
                  onClick={(element) => updateContent(element)}
                  content="Another Republican who wants to stop investigating is former RNC Chair, Michael Steele: âThere is no Spygate because there are no spies in the campaign.â The attitude of Steve Scalise is wishy-washy."
                  style={{
                    backgroundColor: "#F8F8F8",
                    marginBottom: "2px",
                    border: "solid 1px",
                    padding: "15px",
                    fontFamily: "Arial Unicode MS, Arial, sans-serif",
                    fontSize: "13px",
                  }}
                >
                  Another Republican who wants to stop investigating is former
                  RNC Chair, Michael Steele: âThere is no Spygate because there
                  are no spies in the campaign.â The attitude of Steve Scalise
                  is wishy-washy.
                </div>
              </div>
              <div className="col">
                <div
                  id="box"
                  onClick={(element) => updateContent(element)}
                  content="âItâs not necessary to agree with everything he says, itâs not necessary to approve of everything he does, but history will judge him as being on the right side of a struggle between good and evil,â Batten continued."
                  style={{
                    backgroundColor: "#F8F8F8",
                    marginBottom: "2px",
                    border: "solid 1px",
                    padding: "15px",
                    fontFamily: "Arial Unicode MS, Arial, sans-serif",
                    fontSize: "13px",
                  }}
                >
                  âItâs not necessary to agree with everything he says, itâs not
                  necessary to approve of everything he does, but history will
                  judge him as being on the right side of a struggle between
                  good and evil,â Batten continued.
                </div>
                <div
                  id="box"
                  onClick={(element) => updateContent(element)}
                  content="She noticed that Latino voters did record-breaking numbers, especially in states like Florida, Nevada and Arizona â the last of which she describes as âa key state for us.â She brags that the company used its power to ensure that millions of people saw certain hashtags and social media impressions, with the goal of influencing their behavior during the election.â"
                  style={{
                    backgroundColor: "#F8F8F8",
                    marginBottom: "2px",
                    border: "solid 1px",
                    padding: "15px",
                    fontFamily: "Arial Unicode MS, Arial, sans-serif",
                    fontSize: "13px",
                  }}
                >
                  She noticed that Latino voters did record-breaking numbers,
                  especially in states like Florida, Nevada and Arizona â the
                  last of which she describes as âa key state for us.â She brags
                  that the company used its power to ensure that millions of
                  people saw certain hashtags and social media impressions, with
                  the goal of influencing their behavior during the election.â
                </div>
                <div
                  id="box"
                  onClick={(element) => updateContent(element)}
                  content="Meanwhile, Google has yet to answer why their search results for the word âIdiotâ are vastly different from DuckDuckGo."
                  style={{
                    backgroundColor: "#F8F8F8",
                    marginBottom: "2px",
                    border: "solid 1px",
                    padding: "15px",
                    fontFamily: "Arial Unicode MS, Arial, sans-serif",
                    fontSize: "13px",
                  }}
                >
                  Meanwhile, Google has yet to answer why their search results
                  for the word âIdiotâ are vastly different from DuckDuckGo.
                </div>
              </div>
              <div className="col">
                <div
