import React, { useState } from "react";
import Button from "./button";
import "../styles/textInput.css";
import axios from "axios";
import { Link, useHistory } from "react-router-dom";
import { send } from "process";
const { exec } = require("child_process");
export var data;
//const cp = require('child_process');

function App() {
  let history = useHistory();
  const [input,setInput]=useState("")
  const ColoredLine = ({ color }) => (
    <hr
        style={{
            color: color,
            backgroundColor: color,
            height: 2,
            width:900
        }}
    />
);

  
  function sendData() {
    let img = document.getElementById("loading");
    img.style.display = "flex";

    let div = document.getElementById("divF");
    div.style.display = "none";

    let h1 = document.getElementsByTagName("h1");
    for (let i = 0; i < h1.length; i++) {
      h1[i].style.display = "none";
    }
    let h2= document.getElementsByTagName("h2");
    for (let i = 0; i < h1.length; i++) {
      h2[i].style.display = "none";
    }
   
    console.log("data2" + input);
  if(!input.trim()){
    data=" "
    history.push("/SnippetsDetection2");

  }else{
    axios
      .post("http://127.0.0.1:5000/post", {
        id: 0,
        text: input,
        headers: { "Access-Control-Allow-Origin": "*" },
      })
      .then((response) => {
        setTimeout(function () {
          console.log("response \n");
          console.log(response);
          //Reponse=response.data
          data = response.data;
          history.push("/SnippetsDetection2");
        }, 5000);

  
      })
     
      .catch((error) => {
        console.log(error);
      });
    }
  }


  return (
    <>
      <div>
        <h1 className="test"
          style={{
            display: "flex",
            justifyContent: "center",
            marginTop:"3%",
            width:"3em"
          }}
        >
          Snippets Detection
        </h1>
        <h2 style={{
            display: "flex",
            justifyContent: "center",
            marginBottom: "6%",
            fontSize:'25px',
          }}>Just type in some text or copy/paste it, push the button</h2>
      </div>
      <div
        id="divF"
        style={{
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
          flexDirection: "column",
        }}
      >
       
        <textarea value={input} className="textArea" onChange={(e)=>{
          setInput(e.target.value)
          console.log(e.target.value);
      
        }} />
        <div>
          <Button
            id="boutonData"
            disabled={input === ""}
            onClick={() => sendData()}
          >
            Analyze
          </Button>

          <Link to="/">
            <Button
              style={{
                width: "98.48px",
              }}
            >
              back
            </Button>
          </Link>

        </div>
        <ColoredLine color="blue"/>   
        <h3>Textual examples</h3>
        <h4 style={{fontSize:"1rem",marginBottom:"25px"}}>Copy and paste an example below to give a try to identify propaganda snippets.</h4>
        <div style={{width:"96%",marginRight:"-58%"}}>
          <div id="box" content="Manchin says Democrats acted like babies at the SOTU (video) Personal Liberty Poll Exercise your right to vote." onClick={(element) => updateContent(element)} style={{backgroundColor:"#F8F8F8",width:"40%",marginBottom:"2px",border:"solid 1px",padding:"15px",fontFamily:"Arial Unicode MS, Arial, sans-serif",fontSize:"13px"}}>Manchin says Democrats acted like babies at the SOTU (video) Personal Liberty Poll Exercise your right to vote.
</div>
          <div id="box" onClick={(element) => updateContent(element)} content="Democrat West Virginia Sen. Joe Manchin says his colleagues’ refusal to stand or applaud during President Donald Trump’s State of the Union speech was disrespectful and a signal that the party is more concerned with obstruction than it is with progress." style={{backgroundColor:"#F8F8F8",width:"40%",marginBottom:"2px",border:"solid 1px",padding:"15px",fontFamily:"Arial Unicode MS, Arial, sans-serif",fontSize:"13px"}}>Democrat West Virginia Sen. Joe Manchin says his colleagues’ refusal to stand or applaud during President Donald Trump’s State of the Union speech was disrespectful and a signal that the party is more concerned with obstruction than it is with progress.</div>
          <div id="box" onClick={(element) => updateContent(element)} content="In a glaring sign of just how stupid and petty things have become in Washington these days, Manchin was invited on Fox News Tuesday morning to discuss how he was one of the only Democrats in the chamber for the State of the Union speech not looking as though Trump killed his grandma." style={{backgroundColor:"#F8F8F8",width:"40%",marginBottom:"20px",border:"solid 1px",padding:"15px",fontFamily:"Arial Unicode MS, Arial, sans-serif",fontSize:"13px"}}>In a glaring sign of just how stupid and petty things have become in Washington these days, Manchin was invited on Fox News Tuesday morning to discuss how he was one of the only Democrats in the chamber for the State of the Union speech not looking as though Trump killed his grandma.</div>
        

          </div>  
        {/*console.log(data)*/}
      </div>
    </>
  );
  function updateContent(params) {
    console.log(params.target.getAttribute("content"))
    setInput(params.target.getAttribute("content"));
    window.scrollTo({
      top: 0,
      left: 0,
      behavior: "smooth"
  })
    }
}


export default App;
