import React from 'react'
import "../styles/service1_1.css"


class Chkbox extends React.Component{
    constructor(props) {
        super(props);
        this.identifiant=this.props.identifiant;
        this.color=this.props.color;
        this.checked=true;
        this.label=this.props.label;
      }
    render(){
        return(
<tr><td><input name={this.identifiant} type="checkbox"  onClick={() => this.changerCouleur(this.identifiant,this.color,this.label)} defaultChecked={this.checked}/> <mark id={this.identifiant}>{this.props.name}</mark></td></tr>
                
               
           

           

       );
    }
  
    changerCouleur(name,color,labelc){
        let colors=["yellow","red","green","magenta","maroon","sienna","orange","lime","#9B5D9B","aqua","pink","#8486D4","#5A6309","#D1C1F0"]

        var textinputs = document.querySelectorAll('input[type=checkbox]'); 
        //console.log("TextInputs"+textinputs.length)
       /* var empty = [].filter.call( textinputs, function( el ) {
            return !el.checked
         });*/
        /* let identifiantNotCheckd=[]
         for(let i=0;i<empty.length;i++){
            identifiantNotCheckd = empty[i].name.substring("propaganda".length)
         }
      let identifiant=name.substring("propaganda".length)*/
    if(document.querySelector('input[name="'+name+'"]').checked===true){
        let span = document.querySelectorAll("#res #"+name);
        
        span.forEach(function(sp) {
            sp.style.backgroundColor=color;
            sp.title=sp.getAttribute("title2")
     });
     span=document.querySelectorAll("#res #propaganda99");
     span.forEach(function(sp) {
        console.log("labels "+sp.getAttribute("labels")) 
        let labels=sp.getAttribute("labels").split(',')
        console.log("labels ajout "+labels)
        let labels2=sp.getAttribute("labels2").split(',')
        console.log(labels)
        console.log('taille '+labels.length)
        if(labels2.includes(labelc)){
            labels.push(labelc)
        }
        sp.setAttribute("labels",labels)

        if(labels.length===1){
            sp.style.backgroundColor=color;
            //sp.title=sp.getAttribute("title2")

        }else if(labels.length===1){
            sp.style.backgroundColor="white";

        }else{
            sp.style.backgroundColor="#fcf8e3";

        }
       /* sp.style.backgroundColor=color;
        sp.title=sp.getAttribute("title2")*/
 });
     
    }else{
        let span = document.querySelectorAll("#res #"+name);
    
        span.forEach(function(sp) {
            sp.style.backgroundColor="white"
            sp.removeAttribute("title")
     });
     span=document.querySelectorAll("#res #propaganda99");
     span.forEach(function(sp) {
        let labels=sp.getAttribute("labels").split(',')
        console.log("labels suppr "+labels)

        let labels2=sp.getAttribute("labels2").split(',')
        console.log(labels2)
        console.log(labelc)
        if(labels2.includes(labelc)){
            let indice=labels.indexOf(labelc)
            labels.splice(indice,1)
        }
        //console.log("labels new "+labels)
        sp.setAttribute("labels",labels)

        if(labels.length===1){
            //console.log("labels 0 "+labels[0])
            sp.style.backgroundColor=colors[labels[0]-1];
            //sp.title=sp.getAttribute("title2")

        }else if(labels.length===0){
            sp.style.backgroundColor="white"

        }
        else{
            sp.style.backgroundColor="#fcf8e3";

        }
       /* sp.style.backgroundColor=color;
        sp.title=sp.getAttribute("title2")*/
 });
   
    }
    //console.log("CHECKED "+document.querySelector('input[name="'+name+'"]').checked)
    }

}

export default Chkbox;
