import React from 'react';
//import {Reponse} from '../components/textInput'
import data from "../json/jsonmockV3F.json"
import "../styles/service1_1.css"
import Button from '../components/button';
import Multiplechkbox from '../components/MultipleChkbox';
import {Link} from "react-router-dom";



const Service1_1 = () => {
    return (
      <div
>
        <div>
    
        
      <div id="res" style={{
      position: 'absolute',
      right:'15%',
      }}>{texte2()} 
      <div id="btn">
      <Link to="/" ><Button style={{
        
        position: 'relative',
        left: '50%',
        marginTop:'2em'
        
        
      }}>Retour</Button>
      </Link>
      
      </div>
         

      </div> 
      <div style={{
        
        position: 'absolute',
        left: '80%',      
        
      }}>
           <Multiplechkbox/>
         </div>
         { /* <mark>test</mark>*/}
      </div>
      
    </div>
    );
  };
  
  
function texte2(){
  let propagandas=["Appeal_to_Authority","Appeal_to_fear-prejudice","Bandwagon,Reductio_ad_hitlerum","Black-and-White_Fallacy","Causal_Oversimplification","Doubt","Exaggeration,Minimisation","Flag-Waving","Loaded_Language","Name_Calling,Labeling","Repetition","Slogans","Thought-terminating_Cliches","Whataboutism,Straw_Men,Red_Herring"]
  let spans=[]
  let res=[]
 // let taillePropaganda=0;
  let tailleSpan=0;
  let text=""
  for(let i in data.article701225819){
 // }
  //Pour chaque propagande
  //for(let i=1;i<taillePropaganda+1;i++){
    for(let j in data.article701225819[i]){
      j=j-1
      j=j+1
      tailleSpan++;
    }
    text=data.article701225819[i].text
    for(let v=1;v<tailleSpan;v++){
      spans.push(data.article701225819[i]["span_"+v])

    }
    spans.sort(function compare(a, b) {
      if (a.start_char < b.start_char )
         return -1;
      if (a.start_char > b.start_char )
         return 1;
      return 0;
    }); 
    let curseur=0;
    for(let v=0;v<spans.length;v++){
      let spanCourant=spans[v]
      let spanText=spans[v].span
      //console.log(spanText)
      //1 span par phrase
      if(tailleSpan-1===1){
        res.push(text.substring(0,spanCourant.start_char))
        let id="propaganda"+spans[v].label
        let label1=[]
        label1.push(spans[v].label)
        let title="Propaganda: "+propagandas[spans[v].label-1]+"\nProbability: "+spans[v].probability
        res.push(<mark key={v+id+label1+title} id={id} labels={label1} title={title} title2={title}>{spanText}</mark>)
        res.push(text.substring(spanCourant.end_index,text.length)+" ")

      }else{
        //On regarde si ça se chevauche
        let over=0;
        if(v+1<spans.length){
          let spanSuivant=spans[v+1]
          
          if(overlap(spanCourant,spanSuivant)){
            let SpansOverlap=[]
             /*SpansOverlap.push(spanCourant)
             SpansOverlap.push(spanSuivant)*/
             let j=v;
             while(j+1<spans.length){
              SpansOverlap.push(spans[j])
              SpansOverlap.push(spans[j+1])
              j++;
              
             }
             console.log("SPANS OVERLAP "+SpansOverlap[0].span)

             console.log("SPANS OVERLAP "+SpansOverlap[1].span)
             SpansOverlap.sort(function compare(a, b) {
              if (a.start_char < b.start_char )
                 return -1;
              if (a.start_char > b.start_char )
                 return 1;
              return 0;
            }); 
        
            //console.log("CURSEUR "+curseur)
            intervalles(SpansOverlap,text,res,curseur);
            v=v+SpansOverlap.length;


            over=1;
          }
         
          

        }
        if(over!==1){
          over=0;
          let id="propaganda"+spanCourant.label
          //console.log(text.substring(curseur,spanCourant))
          //console.log(text)
          let label1=[]
          label1.push(spanCourant.label)
          let title="Propaganda: "+propagandas[spans[v].label-1]+"\nProbability: "+spans[v].probability

          res.push(text.substring(curseur,spanCourant.start_char))
          res.push(<mark key={v+id+label1+title} id={id} labels={label1} title={title} title2={title}>{spanCourant.span} </mark>)
          curseur=spanCourant.end_index;
        }
      }
    }
    
    curseur=0;
    tailleSpan=0;
    spans.splice(0,spans.length)
  }
  return res;
 // console.log(data.article701225819[propagandas[0]]["span_1"].probability);

        
  //console.log(data[0].span_1)
}

  
  
  /*function duplicateSpan(tab,span){
    for(let i=0;i<tab.length;i++){
      if(tab[i]===span){
        return true;
      }
    }
   
  }*/
  function overlap(span1,span2){
      if((span2.start_char>=span1.start_char && span2.start_char<=span1.end_index)||(span2.end_index>=span1.start_char && span2.end_index<=span1.end_index)){
        return true;
      }

  }
  function intervalles(spans,texte,res,curseur){
   let inter=[]
   for(let i=0;i<spans.length;i++){
     inter.push(spans[i].start_char)
     inter.push(spans[i].end_index)
   }
   const byValue = (a,b) => a - b;
   const sorted=[...inter].sort(byValue);
   //console.log("TEXTE "+texte)
   //console.log("TEST2 "+sorted[0])
   res.push(texte.substring(curseur,sorted[0]))
   //console.log("TEST "+texte.substring(curseur,spans[0].start_char))
   curseur=sorted[0];
   let labels1=[]
   
   for(let i=1;i<sorted.length;i++){
    labels1=labels(spans,curseur+1);

    
    //console.log("CURSEUR +1 "+(curseur+1))
    if(labels1.length===1){
      let id="propaganda"+labels1[0];
      if(curseur!==sorted[i]){

        res.push(<mark key={id+labels(spans,curseur+1)} id={id} labels={labels(spans,curseur+1)}>{texte.substring(curseur,sorted[i])}</mark>)

      }
    }else if(labels1.length===0){
      res.push(texte.substring(curseur,sorted[i]))

    }
    else{
      
      let id="propaganda"+99;
      if(curseur!==sorted[i]){
        console.log(labels1)
        res.push(<mark key={id+labels(spans,curseur+1)} id={id} labels={labels(spans,curseur+1)} >{texte.substring(curseur,sorted[i])}</mark>)

      }
      

    }
    curseur=sorted[i];
    labels1.splice(0,labels1.length)

   }
   labels1=labels(spans,curseur+1);
   if(labels1.length===0){
     res.push(texte.substring(curseur,texte.length))
   }else if(labels1.length===1){
    let id="propaganda"+labels1[0];

    res.push(<mark key={id+labels(spans,curseur+1)} id={id} labels={labels(spans,curseur+1)} title="test">{texte.substring(curseur,texte.length)}</mark>)
   }else{
    let id="propaganda"+99;

    res.push(<mark key={id+labels(spans,curseur+1)} id={id} labels={labels(spans,curseur+1)} title="test">{texte.substring(curseur,texte.length)}</mark>)
   }
  }

  function labels(spans,curseur){
    let labels=[]
    for(let i=0;i<spans.length;i++){
      if(curseur>=spans[i].start_char && curseur<=spans[i].end_index){
        labels.push(spans[i].label)
      }
    }
    return labels;
  }
  export default Service1_1;
