    })();*/
  
      
  }
  return (
    <div style={{
        display: 'flex',  justifyContent:'center', alignItems:'center', height: '100vh',flexDirection:'column'
      }}>
     {
       print?
       <h1> {data}</h1>
       :null
     }
    <textarea class="textArea" onChange={getData} />
    <Button onClick={()=>sendData()} >Analyze</Button>
    {/*console.log(data)*/}
    </div>
  );
}
export default App;
