import React, { useState } from "react";
import Button from "./button";
import "../styles/textInput.css";
import axios from "axios";
import { Link, useHistory } from "react-router-dom";
import { send } from "process";
const { exec } = require("child_process");
export var data;
//const cp = require('child_process');

function App() {
  let history = useHistory();
  const [input,setInput]=useState("")
  const ColoredLine = ({ color }) => (
    <hr
        style={{
            color: color,
            backgroundColor: color,
            height: 2,
            width:900
        }}
    />
);

  
  function sendData() {
    let img = document.getElementById("loading");
    img.style.display = "flex";

    let div = document.getElementById("divF");
    div.style.display = "none";

    let h1 = document.getElementsByTagName("h1");
    for (let i = 0; i < h1.length; i++) {
      h1[i].style.display = "none";
    }
    let h2= document.getElementsByTagName("h2");
    for (let i = 0; i < h1.length; i++) {
      h2[i].style.display = "none";
    }
   
    console.log("data2" + input);
  if(!input.trim()){
    data=" "
    history.push("/SnippetsDetection2");

  }else{
    axios
      .post("http://127.0.0.1:5000/post", {
        id: 0,
        text: input,
        headers: { "Access-Control-Allow-Origin": "*" },
      })
      .then((response) => {
        setTimeout(function () {
          console.log("response \n");
          console.log(response);
          //Reponse=response.data
          data = response.data;
          history.push("/SnippetsDetection2");
        }, 5000);

  
      })
     
      .catch((error) => {
        console.log(error);
      });
    }
  }


  return (
    <>
      <div>
        <h1
          style={{
            display: "flex",
            justifyContent: "center",
            marginTop:"3%"
          }}
        >
          Snippets Detection
        </h1>
        <h2 style={{
            display: "flex",
            justifyContent: "center",
            marginBottom: "6%",
            fontSize:'25px',
          }}>Just type in some text or copy/paste it, push the button</h2>
      </div>
      <div
        id="divF"
        style={{
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
          flexDirection: "column",
        }}
      >
       
        <textarea value={input} className="textArea" onChange={(e)=>{
          setInput(e.target.value)
          console.log(e.target.value);
      
        }} />
        <div>
          <Button
            id="boutonData"
            disabled={input === ""}
            onClick={() => sendData()}
          >
            Analyze
          </Button>

          <Link to="/">
            <Button
              style={{
                width: "98.48px",
              }}
            >
              back
            </Button>
          </Link>

        </div>
        <ColoredLine color="blue"/>   
        <div style={{width:"96%"}}>
          <div style={{backgroundColor:"grey",width:"40%"}}>Test c'est un test</div>
          <div style={{backgroundColor:"grey",width:"40%"}}>Test c'est un test</div>
          <div style={{backgroundColor:"grey",width:"40%"}}>Test c'est un test</div>

          </div>  
        {/*console.log(data)*/}
      </div>
    </>
  );
}

export default App;
