function creerChckBox(){
  let propagandas=["Appeal_to_Authority","Appeal_to_fear-prejudice","Bandwagon,Reductio_ad_hitlerum","Black-and-White_Fallacy","Causal_Oversimplification","Doubt","Exaggeration,Minimisation","Flag-Waving","Loaded_Language","Name_Calling,Labeling","Repetition","Slogans","Thought-terminating_Cliches","Whataboutism,Straw_Men,Red_Herring"]
  let colors=["yellow","red","green","magenta","maroon","sienna","orange","lime","#9B5D9B","aqua","pink","#8486D4","#5A6309","#D1C1F0"]
  let chckBoxes=[]
  //let taillePropaganda=0;
  let tailleSpan=0;
  let spans=[]
  let labels=[]
  let sans_doublons
  let name=""
  for(let i in data){
    name=i
  }
  for(let i in data[name]){
      //taillePropaganda++;
   // }
 // for(let i=1;i<taillePropaganda+1;i++){
      tailleSpan=Object.keys(data[name][i]).length

      for(let v=1;v<tailleSpan;v++){
        spans.push(data[name][i]["span_"+v])
  
      }
      spans.sort(function compare(a, b) {
        if (a.start_char < b.start_char )
           return -1;
        if (a.start_char > b.start_char )
           return 1;
        return 0;
      }); 
      for(let v=0;v<spans.length;v++){
          if(typeof(spans[v].label)!=='object'){
            labels.push(spans[v].label)

          }else{
            for(let p=0;p<spans[v].label.length;p++){
              labels.push(spans[v].label[p])

            }

          }
      }
      tailleSpan=0;
      sans_doublons = Array.from(new Set(labels));
  }
  var index = sans_doublons.indexOf(0);
  if (index > -1) {
    sans_doublons.splice(index, 1);
  }
  for(let i=0;i<sans_doublons.length;i++){
   chckBoxes.push(<Chkbox key={i} name={propagandas[sans_doublons[i]-1]} identifiant={'propaganda'+sans_doublons[i]} color={colors[sans_doublons[i]-1]} onClick={() => changerCloud('propaganda'+sans_doublons[i],this.color)}/>)
  }
 /* chckBoxes.push(<Chkbox name="propaganda 1" identifiant='propaganda1' />)
  chckBoxes.push(<Chkbox name="propaganda 2"  identifiant='propaganda2' />)
  chckBoxes.push(<Chkbox name="propaganda 3" identifiant='propaganda3' />)*/
  return chckBoxes;
  

}
export function changerCloud(name,color){

  for(let i=0;i<word.length;i++){

  
  if(document.querySelector('input[name="'+name+'"]').checked===true){
    if(color===word[i].color){
      let text=document.querySelectorAll("g text")
      console.log("taille "+text.length)
        for(let y=0;y<text.length;y++){
          console.log("couleur "+text[y].style.fill)
          console.log("get attribute "+text[y].getAttribute("fill"))
          if(text[y].getAttribute("fill")===color || color===RGBToHex(text[y].getAttribute("fill")).toUpperCase()){
            text[y].style.display='block'
          }
        }
      //console.log(text[0].style.fill)


  }
  }else{
      //console.log("COLOR "+word[i].color)
      if(color===word[i].color){
          let text=document.querySelectorAll("g text")
            for(let y=0;y<text.length;y++){
              
              if(text[y].getAttribute("fill")===color || color===RGBToHex(text[y].getAttribute("fill")).toUpperCase()){
                text[y].style.display='none'
              }
            }
          //console.log(text[0].style.fill)


      }
  }
}


