import React ,{useState}from 'react'
import Button from './button';
import "../styles/textInput.css";
import axios from 'axios'
import { Link} from "react-router-dom";
import { Redirect } from 'react-router'


export var Reponse

function App() {
 
const [data,setData]=useState(null)
const [print,setPrint]=useState(false)


  function getData(val)
  {
    console.log(val.target.value)
    setData(val.target.value)
    setPrint(false)
  }
  function sendData(){
 
    /*axios.post('https://jsonplaceholder.typicode.com/posts',{'data':data})*/
    axios.post('http://127.0.0.1:5000/post',{'data':data,
    headers: {'Access-Control-Allow-Origin' : '*'}})
    .then(response=>{
      Reponse=<p>{response.data}</p>
     // console.log(reponse)
     this.props.router.push('/')
     
      console.log(response.data)
      console.log(response);
    })
    /*.then(() => this.setState({ redirect: true }))*/
    .catch(error=>{
      console.log(error);
    })
  }

  /*async function getDonnees() {
    const reponse = await axios.get('http://127.0.0.1:5000/post')
    console.log(reponse.data)
}*/
  return (
    <div style={{
        display: 'flex',  justifyContent:'center', alignItems:'center', height: '100vh',flexDirection:'column'
      }}>
     {
       print?
       <h1> {data}</h1>
       :null
     }
    <textarea className="textArea" onChange={getData} />
    
      <Button onClick={()=>sendData()} >Analyze</Button>
      <Link to="/service1_1" >

      <Button >Analyze2</Button>

    </Link>
    {/*console.log(data)*/}
    </div>
  );
}
export default App;
